        "parameters": {
          "jsCode": "// n8n Code node — Mode: Run Once for All Items\n// Output: [{ json: { email: { ...minimal fields... } } }, ...]\n\n/* ----------------- helpers ----------------- */\nconst b64urlToUtf8 = (s = '') => {\n  try { return Buffer.from(s.replace(/-/g,'+').replace(/_/g,'/'),'base64').toString('utf8'); }\n  catch { return ''; }\n};\n\nconst decodeEntities = (s = '') => {\n  const named = {\n    '&amp;':'&','&lt;':'<','&gt;':'>','&quot;':'\"','&#39;':\"'\",'&nbsp;':' ',\n    '&zwnj;':'','&zwj;':''\n  };\n  let out = String(s);\n  // do two passes in case of double-encoding\n  for (let i=0;i<2;i++) {\n    const prev = out;\n    out = out\n      .replace(/&(amp|lt|gt|quot|#39|nbsp|zwnj|zwj);/gi, m => named[m.toLowerCase()] ?? m)\n      .replace(/&#(\\d+);/g, (_,d)=>String.fromCharCode(parseInt(d,10)))\n      .replace(/&#x([0-9a-fA-F]+);/g, (_,h)=>String.fromCharCode(parseInt(h,16)));\n    if (out === prev) break;\n  }\n  return out;\n};\n\nconst stripHtml = (html = '') => {\n  // remove script/style, convert some blocks to line breaks, strip remaining tags\n  html = html.replace(/<script[\\s\\S]*?<\\/script>/gi,'')\n             .replace(/<style[\\s\\S]*?<\\/style>/gi,'')\n             .replace(/<\\/(p|div|li|tr|h[1-6])>/gi,'\\n')\n             .replace(/<br\\s*\\/?>/gi,'\\n')\n             .replace(/<\\/?[^>]+>/g,' ');\n  return decodeEntities(html);\n};\n\nconst clean = (t = '') => t\n  // strip zero-width & invisibles\n  .replace(/[\\u200B-\\u200D\\u2060\\uFEFF]/g,'')\n  // normalize whitespace\n  .replace(/\\s+\\n/g,'\\n').replace(/\\n\\s+/g,'\\n').replace(/[ \\t]{2,}/g,' ')\n  // trim quoted replies (naive)\n  .replace(/On .* wrote:.*$/is,'')\n  // drop footer noise / unsubscribe tails\n  .replace(/(unsubscribe|manage preferences|update preferences|privacy policy)[\\s\\S]*$/i,'')\n  // compress super-long tracking URLs\n  .replace(/https?:\\/\\/\\S{80,}/g,'[link]')\n  .trim();\n\n/* Gmail payload helpers */\nconst findPart = (node, includesMime) => {\n  if (!node) return null;\n  if (node.mimeType?.includes(includesMime) && node.body?.data) return node.body.data;\n  if (Array.isArray(node.parts)) {\n    for (const p of node.parts) {\n      const found = findPart(p, includesMime);\n      if (found) return found;\n    }\n  }\n  return null;\n};\n\n/* choose the best body source in your dataset */\nconst pickBody = (e) => {\n  // your dataset (per screenshot) contains html/text/textAsHtml directly\n  if (e.html) return { kind: 'html', data: e.html };\n  if (e.text) return { kind: 'plain', data: e.text };\n  if (e.textAsHtml) return { kind: 'html', data: e.textAsHtml };\n\n  // fallbacks for Gmail-like payloads\n  const p = e.payload || {};\n  if (p.body?.data) return { kind: p.mimeType?.includes('html') ? 'html' : 'plain', data: b64urlToUtf8(p.body.data) };\n  const plain = findPart(p, 'text/plain');\n  if (plain) return { kind: 'plain', data: b64urlToUtf8(plain) };\n  const html = findPart(p, 'text/html');\n  if (html) return { kind: 'html', data: b64urlToUtf8(html) };\n\n  // last resort\n  if (e.snippet) return { kind: 'plain', data: e.snippet };\n  if (typeof e.body === 'string') return { kind: 'plain', data: e.body };\n  return { kind: 'plain', data: '' };\n};\n\n/* ----------------- main ----------------- */\nconst out = [];\n\nfor (const item of $input.all()) {\n  const e = item.json;\n\n  // subject/from/date\n  const subjectRaw = e.subject ?? e.headers?.Subject ?? '';\n  const fromRaw = e.from ?? e.headers?.from ?? '';\n  const dateRaw = e.date ?? e.internalDate ?? e.headers?.Date ?? null;\n\n  // body: prefer HTML → strip; else plain → decode entities; then clean & cap\n  const bodyPick = pickBody(e);\n  let bodyRaw = bodyPick.kind === 'html' ? stripHtml(bodyPick.data) : decodeEntities(bodyPick.data);\n  const body = clean(bodyRaw).slice(0, 1200); // keep compact for LLM\n\n  // misc\n  const headersStr = typeof e.headers === 'string' ? e.headers : JSON.stringify(e.headers || {});\n  const from = String(fromRaw || '').toLowerCase();\n  const subject = String(subjectRaw || '');\n\n  const minimalEmail = {\n    subject: subject.slice(0,160),\n    from,\n    date: dateRaw,\n    labels: e.labelIds || e.labels || [],\n    threadId: e.threadId || '',\n    // light heuristics that also help you pre-route before LLM\n    hasQuestion: /(\\?|can you|please|let me know|confirm|approve|could you|would you|do you)/i.test(body),\n    isNoReply: /no-?reply|donotreply/i.test(from),\n    isBulk: /(list-id|auto-submitted|precedence:\\s*(bulk|list))/i.test(headersStr),\n    attachments: (e.attachments || []).map(a => ({ name: String(a.filename || '').slice(0,80) })),\n    text: body\n  };\n\n  out.push({ json: { email: minimalEmail } });\n}\n\nreturn out;\n"
