// Determine notification frequency based on trigger source
const triggerNode = $('Daily Schedule (7AM)').first() || 
                     $('Weekly Schedule (Mon 7AM)').first() || 
                     $('Webhook Trigger').first();

let frequency = 'daily';
if ($('Weekly Schedule (Mon 7AM)').first()) {
  frequency = 'weekly';
}

// Check for manual frequency override from webhook query params
if ($('Webhook Trigger').first()?.json?.query?.frequency) {
  frequency = $('Webhook Trigger').first().json.query.frequency;
}

return [{ json: { frequency } }];

// Get alert rules and group by user_id
const rules = $input.all().map(item => item.json).flat();

// Group rules by user_id
const userRulesMap = {};
rules.forEach(rule => {
  if (!userRulesMap[rule.user_id]) {
    userRulesMap[rule.user_id] = [];
  }
  userRulesMap[rule.user_id].push(rule);
});

// Create output array with one item per user
const usersWithRules = Object.entries(userRulesMap).map(([userId, userRules]) => ({
  user_id: userId,
  rule_ids: userRules.map(r => r.id),
  rule_count: userRules.length
}));

return usersWithRules.map(user => ({ json: user }));

// Merge user email with alerts data
const userEmail = $('Get User Email').first().json[0];
const groupData = $('Group Rules by User').first().json;
const alerts = $input.first().json;

return [{
  json: {
    user_id: groupData.user_id,
    rule_ids: groupData.rule_ids,
    email: userEmail?.email || null,
    name: userEmail?.raw_user_meta_data?.name || null,
    alerts: alerts
  }
}];

// Get alerts and property details from previous nodes
const mergedData = $('Merge User Data').first().json;
const alerts = mergedData.alerts || [];
const properties = $('Fetch Property Details').first().json || [];
const userEmail = mergedData.email;
const userName = mergedData.name || (userEmail ? userEmail.split('@')[0] : 'Investor');
const ruleIds = mergedData.rule_ids || [];

// Build property lookup map
const propertyMap = {};
properties.forEach(p => {
  propertyMap[p.id] = p;
});

// Merge alerts with property details
const alertsWithDetails = alerts.map(alert => {
  const property = propertyMap[alert.property_id] || {};
  return {
    ...alert,
    property
  };
});

// Sort by match score descending
alertsWithDetails.sort((a, b) => b.match_score - a.match_score);

// Generate email HTML
const emailHtml = `
<!DOCTYPE html>
<html>
<head>
  <meta charset="utf-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <style>
    body { font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif; line-height: 1.6; color: #333; background-color: #f5f5f5; margin: 0; padding: 0; }
    .container { max-width: 600px; margin: 0 auto; background-color: #ffffff; }
    .header { background-color: #2563eb; color: #ffffff; padding: 24px; text-align: center; }
    .header h1 { margin: 0; font-size: 24px; }
    .content { padding: 24px; }
    .summary { background-color: #eff6ff; border-left: 4px solid #2563eb; padding: 16px; margin-bottom: 24px; }
    .property-card { border: 1px solid #e5e7eb; border-radius: 8px; padding: 16px; margin-bottom: 16px; background-color: #ffffff; }
    .property-header { display: flex; justify-content: space-between; align-items: center; margin-bottom: 12px; }
    .property-score { background-color: #10b981; color: #ffffff; padding: 4px 12px; border-radius: 12px; font-weight: bold; font-size: 14px; }
    .property-score.high { background-color: #10b981; }
    .property-score.medium { background-color: #3b82f6; }
    .property-score.low { background-color: #6b7280; }
    .property-title { font-size: 18px; font-weight: bold; color: #111827; margin: 0; }
    .property-details { font-size: 14px; color: #6b7280; margin: 8px 0; }
    .property-stats { display: grid; grid-template-columns: 1fr 1fr; gap: 8px; margin: 12px 0; }
    .stat { background-color: #f9fafb; padding: 8px; border-radius: 4px; }
    .stat-label { font-size: 12px; color: #6b7280; }
    .stat-value { font-size: 16px; font-weight: bold; color: #111827; }
    .cta-button { display: inline-block; background-color: #2563eb; color: #ffffff; padding: 12px 24px; text-decoration: none; border-radius: 6px; font-weight: bold; margin: 24px 0; }
    .footer { background-color: #f9fafb; padding: 16px 24px; text-align: center; font-size: 12px; color: #6b7280; }
    .footer a { color: #2563eb; text-decoration: none; }
  </style>
</head>
<body>
  <div class="container">
    <div class="header">
      <h1>üéØ New Property Alerts</h1>
    </div>
    <div class="content">
      <p>Hi ${userName},</p>
      <div class="summary">
        <strong>${alertsWithDetails.length} new ${alertsWithDetails.length === 1 ? 'property matches' : 'properties match'} your investment criteria!</strong>
      </div>
      ${alertsWithDetails.slice(0, 10).map(alert => {
        const p = alert.property;
        const scoreClass = alert.match_score >= 80 ? 'high' : alert.match_score >= 60 ? 'medium' : 'low';
        return `
      <div class="property-card">
        <div class="property-header">
          <h2 class="property-title">${p.parcel || 'Property'}</h2>
          <span class="property-score ${scoreClass}">Score: ${alert.match_score}</span>
        </div>
        <div class="property-details">
          üìç ${p.full_address || 'Address not available'}<br>
          üìç ${p.county?.name || 'County'}, ${p.county?.state || 'State'}
        </div>
        <div class="property-stats">
          <div class="stat">
            <div class="stat-label">Total Due</div>
            <div class="stat-value">$${(p.total_due || 0).toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</div>
          </div>
          <div class="stat">
            <div class="stat-label">Property Type</div>
            <div class="stat-value">${p.property_type || 'Unknown'}</div>
          </div>
          <div class="stat">
            <div class="stat-label">Acres</div>
            <div class="stat-value">${p.acres || 'N/A'}</div>
          </div>
          <div class="stat">
            <div class="stat-label">Alert Rule</div>
            <div class="stat-value">${alert.alert_rule_name}</div>
          </div>
        </div>
      </div>
        `;
      }).join('')}
      ${alertsWithDetails.length > 10 ? `<p><em>+ ${alertsWithDetails.length - 10} more ${alertsWithDetails.length - 10 === 1 ? 'property' : 'properties'}...</em></p>` : ''}
      <center>
        <a href="https://taxdeedflow.com/properties/alerts/inbox" class="cta-button">View All Alerts</a>
      </center>
    </div>
    <div class="footer">
      <p>You're receiving this email because you have active property alert rules.</p>
      <p><a href="https://taxdeedflow.com/properties/alerts">Manage Alert Settings</a> | <a href="https://taxdeedflow.com/settings/notifications">Unsubscribe</a></p>
      <p>&copy; 2026 Tax Deed Flow. All rights reserved.</p>
    </div>
  </div>
</body>
</html>
`;

return [{
  json: {
    userEmail,
    userName,
    alertCount: alertsWithDetails.length,
    emailHtml,
    emailSubject: `üéØ ${alertsWithDetails.length} New Property ${alertsWithDetails.length === 1 ? 'Alert' : 'Alerts'} - Tax Deed Flow`,
    alerts: alertsWithDetails,
    ruleIds: ruleIds
  }
}];

// Split rule IDs into separate items for batch update
const ruleIds = $json.ruleIds || [];
const emailData = $json;

return ruleIds.map(ruleId => ({
  json: {
    rule_id: ruleId,
    emailData: emailData
  }
}));

// Aggregate all results from previous nodes
const frequency = $('Determine Frequency').first().json.frequency;
const alertRules = $('Get Alert Rules by Frequency').all().map(item => item.json).flat();
const emailsSent = $('Send Email').all().length || 0;
const uniqueUsers = new Set($('Group Rules by User').all().map(item => item.json.user_id)).size;

// Build summary
const summary = {
  generated_at: new Date().toISOString(),
  workflow_type: 'property_alert_email_digest',
  notification_frequency: frequency,

  stats: {
    rules_checked: alertRules.length,
    users_checked: uniqueUsers,
    emails_sent: emailsSent,
    total_alerts_sent: $('Send Email').all().reduce((sum, item) => sum + (item.json.alertCount || 0), 0)
  },

  status: emailsSent > 0 ? 'emails_sent' : 'no_alerts_to_send'
};

return [{ json: summary }];
